        <Th><b>Category Name</b></Th>
        <Th ><b>Action</b></Th>
      </Tr>
    </Thead>
    <Tbody>
      {
        
      }
     
    </Tbody>
    
  </Table>

  <center><p>There are no  categories</p></center>
  </TableContainer>


        </div>

            

    );

}

export default AllCategory;
