import React, { useDState } from "react";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

export default function AddCategory() {


    return (
        

        <Form>
            <Form.Group className="mb-3" controlId="catname">
                <Form.Label>Category Name</Form.Label>
                <Form.Control type="text" placeholder="Enter Category Name" />

            </Form.Group>
            <Form.Group className="mb-3" controlId="catdes">
                <Form.Label>Category Description</Form.Label>
                <Form.Control type="text" placeholder="Enter Category Description" />

            </Form.Group>

            <Form.Group className="mb-3" controlId="status">
                <Form.Label>Category Status</Form.Label>
                <Form.Control type="number" placeholder="Enter status 0 or 1" />

            </Form.Group>




            <Button variant="primary" type="submit">
                Save
            </Button>
        </Form>


    );
}
