import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

export default function AddCategory() {


    const [catName,setCatName]=useState("");
    const [catDesc,setCatDesc]=useState("");
    const [catStatus,setCatStatus]=useState("");

     function sendCategoryData(e){ //pass the event as argument

      e.preventDefault();
      const newCaregory={

        
      }

     }

    return (


        <Form onSubmit={sendCategoryData}>
            <Form.Group className="mb-3" controlId="catname">
                <Form.Label>Category Name</Form.Label>
                <Form.Control  onChange={(e)=>{
                    setCatName(e.target.value);
                }} type="text" placeholder="Enter Category Name" />

            </Form.Group>
            <Form.Group className="mb-3" controlId="catdes">
                <Form.Label>Category Description</Form.Label>
                <Form.Control  onChange={(e)=>{
                    setCatDesc(e.target.value);
                }} type="text" placeholder="Enter Category Description" />

            </Form.Group>

            <Form.Group className="mb-3" controlId="status">
                <Form.Label>Category Status</Form.Label>
                <Form.Control onChange={(e)=>{
                    setCatStatus(e.target.value);
                }} type="number" placeholder="Enter status 0 or 1" />

            </Form.Group>




            <Button variant="primary" type="submit ">
                Save
            </Button>
        </Form>


    );
}
