import { Link } from "react-router-dom";

function Sidebar(){

    const items=[

        {
            id:1,
            name:"DashBoard",
            path:"/"
        },
        {
            id:2,
            name:"Category List",
            path:"/c_list"
        },
        {
            id:1,
            name:"Product List",
            path:"/p_list"
        },
       {
            id:1,
            name:"Stock Manager",
            path:"/s_manager"
        },
        {
            id:1,
            name:"Reports",
            path:"/reports"
        },
        {
            id:1,
            name:"Add Product",
            path:"/add_product"
        },
        {
            id:7,
            name:"My Profile",
            path:"/my_profile"
        }
    ]

    return(

        <div className="sidebar">

            <div></div>

            {items.map((item)=>(
                <Link to={item.path} className="sidebar-item" key={item.id}>{item.name}</Link>
            ))}
             
        </div>

    );

}
export default Sidebar;
