import './App.css';
import AddCategory from './components/AddCategory';
import BasicExample from './components/Header';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom' //as Router
import AdminLayout from './components/AdminLayout';
import Home from './components/Home';
import AllCategory from './components/AllCategory';
import Register from './pages/Register';
import Login from './pages/Login';
import { ChakraProvider } from '@chakra-ui/react'
import { Toaster } from 'react-hot-toast';
import GlobalProvider from './GlobalContext';




function App() {
  return (

<ChakraProvider><BrowserRouter>
          <GlobalProvider></GlobalProvider>
           <div>
                <BasicExample />

           </div>

           <Routes>
               <Route path="/" element={<AllCategory />} />
               <Route path="/add" element={<AddCategory />} />
               <Route path="/register" element={<Register />} />
               <Route path="/login" element={<Login />} />


           </Routes>
       </BrowserRouter>  <Toaster position="bottom-center"
       reverseOrder={false} />   </ChakraProvider>



  );
}

export default App;
